INSERT INTO `app_hrzg_widget_template` VALUES (3,'Header - image','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Header Image Widget\",\n    \"type\": \"object\",\n    \"properties\": {\n        \"parallax\": {\n            \"title\": \"Parallax ON\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\"\n        },\n        \"subline\": {\n            \"type\": \"string\",\n            \"title\": \"Subline\",\n            \"default\": \"subline\"\n        },\n        \"headline\": {\n            \"type\": \"string\",\n            \"title\": \"Headline\",\n            \"default\": \"headline\"\n        },\n        \"imageSource\": {\n            \"type\": \"string\",\n            \"format\": \"filefly\",\n            \"title\": \"Image URL\",\n            \"default\": \"\\/header.png\"\n        },\n        \"image_seo\": {\n            \"type\": \"string\",\n            \"title\": \"Image Seo\"\n        },\n        \"patternSource\": {\n            \"type\": \"string\",\n            \"format\": \"filefly\",\n            \"title\": \"Pattern URL\"\n        }\n    }\n}','<div class=\"header-image text-center text-on-picture{% if parallax == true %} header-fixed{% endif %}\" \r\n    style=\"background-image: url(/img{{ imageSource }},stream); width: 100%;\" \r\n    alt=\"{{ image_seo }}\">\r\n    <div class=\"pattern\" {% if (patternSource) != \"\" %}style=\"background: url(/filefly/api?action=stream&path={{ patternSource }});\"{% endif %}>\r\n        <div class=\"container\">\r\n            {% if (subline) != \"\" %}\r\n                <h5> {{ subline }} </h5>\r\n            {% endif %} \r\n            {% if (headline) != \"\" %}\r\n                <h1> {{ headline }} </h1>\r\n            {% endif %}    \r\n        </div>    \r\n    </div>\r\n</div>',NULL,'2017-10-25 16:33:02');
INSERT INTO `app_hrzg_widget_template` VALUES (4,'Content - text blocks','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Content with Button Widget\",\n    \"type\": \"object\",\n    \"required\": [\n        \"skin\"\n    ],\n    \"properties\": {\n        \"skin\": {\n            \"propertyOrder\": 10,\n            \"type\": \"string\",\n            \"title\": \"Skin style\",\n            \"enum\": [\n                \"default\",\n                \"inverse\"\n            ]\n        },\n        \"text\": {\n            \"propertyOrder\": 20,\n            \"type\": \"string\",\n            \"title\": \"Text orientation\",\n            \"enum\": [\n                \"text-left\",\n                \"text-center\",\n                \"text-right\"\n            ]\n        },\n        \"ckbox\": {\n            \"propertyOrder\": 30,\n            \"title\": \"Container on\\/off\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\",\n            \"default\": true\n        },\n        \"subline\": {\n            \"propertyOrder\": 40,\n            \"type\": \"string\",\n            \"title\": \"Subline\",\n            \"default\": \"Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante.\"\n        },\n        \"headline\": {\n            \"propertyOrder\": 50,\n            \"type\": \"string\",\n            \"title\": \"Headline\",\n            \"default\": \"Senectus et netus et malesuada fames ac turpis\"\n        },\n        \"blocks\": {\n            \"type\": \"array\",\n            \"format\": \"tabs\",\n            \"title\": \"Text\",\n            \"items\": {\n                \"type\": \"object\",\n                \"title\": \"Paragraph\",\n                \"properties\": {\n                    \"text_html\": {\n                        \"propertyOrder\": 100,\n                        \"type\": \"string\",\n                        \"title\": \"Text\",\n                        \"default\": \"Pellentesque habitant 