INSERT INTO `app_hrzg_widget_template` VALUES (17,'Header - video (JS)','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Header Video Widget\",\n    \"type\": \"object\",\n    \"properties\": {\n        \"subline\": {\n            \"type\": \"string\",\n            \"title\": \"Subline\",\n            \"default\": \"Subline\"\n        },\n        \"headline\": {\n            \"type\": \"string\",\n            \"title\": \"Headline\",\n            \"default\": \"Headline\"\n        },\n        \"youtube_id\": {\n            \"type\": \"string\",\n            \"title\": \"YouTube ID\",\n            \"default\": \"28ji6G5hTRw\"\n        },\n        \"imageSource\": {\n            \"type\": \"string\",\n            \"format\": \"filefly\",\n            \"title\": \"Image URL\",\n            \"default\": \"\"\n        }\n    }\n}','{# disableSrc #}\r\n{% set playerId = \'player-\' ~ random() %}\r\n\r\n{% set disableSrc %}\r\n    function resizeHandler() {\r\n        if($(\'#{{ playerId }}\').length) {\r\n            if(window.innerWidth < 768) {\r\n                $(\'#{{ playerId }}\')[0].src = \"\"; \r\n            } else if(!$(\'#player\')[0].src && window.innerWidth >= 768) {\r\n                $(\'#{{ playerId }}\')[0].src = \"https://www.youtube.com/embed/{{ youtube_id }}?rel=0&amp;showinfo=0&amp;controls=0&amp;autoplay=1&amp;loop=1&amp;playlist={{ youtube_id }}&amp;enablejsapi=1&version=3\";\r\n            }\r\n        }\r\n    }\r\n    $(document).ready(function () {\r\n        $(window).resize(function() {\r\n            resizeHandler()\r\n        });\r\n        resizeHandler();\r\n    })\r\n    resizeHandler();\r\n{% endset %}\r\n{{ this.registerJs(disableSrc) }}\r\n\r\n<div class=\"header-video header-image text-center\" style=\"background-image: url(/filefly/api?action=stream&path={{ imageSource }}); width: 100%;\">\r\n    <div style=\"position: relative; max-height: 100%; overflow: hidden; \">\r\n        <div class=\"embed-responsive embed-responsive-16by9\">\r\n            <div class=\"header-video-iframe\"id=\"background\">\r\n               <iframe id=\'{{ playerId }}\' width=\"100%\" height=\"100%\" src=\"https://www.youtube.com/embed/{{ youtube_id }}?rel=0&amp;showinfo=0&amp;controls=0&amp;autoplay=1&amp;loop=1&amp;playlist={{ youtube_id }}&amp;enablejsapi=1&version=3\" frameborder=\"0\"></iframe>\r\n            </div>\r\n        </div>\r\n        <div class=\"video-front text-on-picture\">\r\n            <h5>{{ subline }}</h5>\r\n            <h1>{{ headline }}</h1>\r\n        </div>\r\n            \r\n    </div>\r\n</div>\r\n',NULL,'2017-10-25 09:45:41');
