INSERT INTO `app_less` VALUES (14,'default-main','// Main LESS file\r\n\r\n// Framework and base theme\r\n@import \"/app/vendor/bower/bootstrap/less/bootstrap.less\";\r\n@import \"//code.ionicframework.com/ionicons/2.0.1/css/ionicons.min.css\";\r\n\r\n// Google Fonts \r\n@import url(https://fonts.googleapis.com/css?family=Source+Sans+Pro:100,300,400);\r\n\r\n// Style for widget templates\r\n@import \"default_variables\";\r\n@import \"default_error-page\";\r\n@import \"default_widgets\";\r\n@import \"default_forms\";\r\n@import \"default_ios\";\r\n\r\n.container-layout {\r\n    margin-top: @navbar-height;\r\n}\r\n\r\nh1, h2, h3, h4, h5, h6, .navbar-nav > li > a {\r\n    letter-spacing: 0.05em;\r\n    text-transform: uppercase;\r\n}\r\n\r\np {\r\n    font-weight: 100;\r\n}\r\n');
INSERT INTO `app_less` VALUES (18,'default_widgets','.hrzg-widget-widget {\r\n    \r\n    // Backgrounds and Buttons //\r\n    \r\n    .bg-default {\r\n        background: white;\r\n        .btn-default {\r\n            color: white;\r\n            background-color: @brand-primary;\r\n        }\r\n        .btn-default:hover {\r\n            color: white;\r\n            background-color: @brand-secondary;\r\n            border-color: @brand-secondary;\r\n        }\r\n    }\r\n    \r\n    .bg-inverse {\r\n        background: @brand-secondary;\r\n        color: white;\r\n        height: 100%;\r\n        a {\r\n            color: white;\r\n            &.btn-inverse {\r\n                color: @brand-secondary;\r\n            }\r\n        }\r\n        .btn-inverse {\r\n            background-color: white;\r\n        }\r\n        .btn-inverse:hover {\r\n            color: @brand-primary;\r\n            background-color: white;\r\n            opacity: 0.8;\r\n        }\r\n    }\r\n    \r\n    .text-on-picture {\r\n        background-size: cover;\r\n        background-repeat: no-repeat;\r\n        color: white;\r\n        text-shadow: 0 0 1px rgba(0,0,0,0.05),0 1px 2px rgba(0,0,0,0.3),\r\n    }\r\n    \r\n    // For all widgets (except full width e.g. header and Big Image)\r\n    .widget-content {\r\n        padding: 6vw 0;\r\n        .no-container {\r\n            padding: 0 2em;\r\n        }\r\n    }\r\n       \r\n    // Header Image //\r\n    \r\n    .header-image {\r\n        background-position: center center;\r\n        background-color: @gray-lighter;\r\n        &.header-fixed {\r\n            background-size: cover;\r\n            background-repeat: no-repeat;\r\n            background-attachment: fixed;\r\n            @media (max-width: @screen-xs-max) {\r\n                // parallax with background-attachment: fixed; does not work mobile\r\n                background-attachment: initial;\r\n            }\r\n        }\r\n        .pattern{\r\n            background-color: rgba(0,0,0,0.2) !important;\r\n            padding: 200px 0;\r\n            @media (max-width: @screen-xs-max) {\r\n                padding: 90px 0;\r\n            }\r\n        }\r\n    }\r\n    \r\n    // Header Video //\r\n    \r\n    .header-video {\r\n        &.header-image {\r\n            background-size: cover;\r\n            background-repeat: no-repeat;\r\n            background-position: center center;\r\n            position: relative;\r\n        }\r\n        .video-front {\r\n            text-align: center;\r\n            z-index: 2;\r\n            position: absolute;\r\n            top:40%;\r\n            width: 100%;\r\n        }\r\n        .video-overlay {\r\n            background-color: rgba(0,0,0,0.2) !important;\r\n            position: absolute;\r\n            top: 0;\r\n            height: 100%;\r\n            width: 100%;\r\n            z-index: 1;\r\n        }\r\n        .embed-responsive {\r\n            height: 100%;\r\n        }\r\n    }\r\n    \r\n    // Big Image //\r\n    \r\n    \r\n    .separator-image {\r\n        max-height: 80vh;\r\n        overflow: hidden;\r\n        div#parallax {\r\n            height: 40vh;\r\n            background-color: @gray-lighter;\r\n            background-size: cover;\r\n            background-repeat: no-repeat;\r\n            background-position: center center;\r\n            position: relative;\r\n            background-attachment: fixed;\r\n            @media (max-width: @screen-xs-max) {\r\n                // parallax with background-attachment: fixed; does not work mobile\r\n                background-attachment: initial;\r\n                width: 100%;\r\n                \r\n            }\r\n        }\r\n    }\r\n        \r\n    .big-image {\r\n        margin-top: 40px;\r\n        margin-bottom:40px;\r\n        width: 100%;\r\n    }\r\n    \r\n    // Content & Image //\r\n        \r\n    \r\n    .text-next-image {\r\n        &.text-on-right {\r\n            padding-left: 30px;\r\n            @media (max-width: @screen-sm-max) {\r\n                margin-top: 20px;\r\n                padding-left: 15px;\r\n            }\r\n        }\r\n        &.text-on-left {\r\n            padding-right: 30px;\r\n        }\r\n        h2 {\r\n            margin-bottom: 30px;\r\n        }\r\n    }\r\n    \r\n    \r\n    // Blocks widget //\r\n    \r\n    .blocks-headline {\r\n        margin-bottom: 30px;\r\n    }\r\n    \r\n    .blocks { \r\n       [class^=\"col-\"] { \r\n           margin-bottom: 3em; \r\n       }\r\n       @media (max-width: @screen-sm-max) {\r\n            [class^=\"col-\"] { \r\n                margin-bottom: 3em; \r\n            }\r\n        }\r\n        .img-responsive {\r\n            display: inline-block;\r\n        }\r\n    } \r\n            \r\n    \r\n    // Footer //\r\n    \r\n    .footer {\r\n        background: @brand-secondary;\r\n        padding: 0.9em;\r\n        .list-inline {\r\n            a {\r\n                color: white;\r\n                text-decoration: none;\r\n                margin: 0;\r\n                margin-top: 10px;\r\n                letter-spacing: 0.3em;\r\n                text-transform: uppercase;\r\n                font-weight: 500;\r\n                letter-spacing: 0.1em;\r\n            }\r\n            a:hover {\r\n                color: @brand-primary;\r\n            }\r\n            @media (max-width: @screen-xs-max) {\r\n                text-align: center; \r\n            }\r\n        }\r\n        .footer-logo-position {\r\n            text-align: right;\r\n            @media (max-width: @screen-xs-max) {\r\n                text-align: -webkit-center;\r\n            }\r\n        }\r\n        .footer-logo-herzog {\r\n            width: 20px;\r\n            img {\r\n                height: 20px;\r\n            }\r\n            @media (max-width: @screen-xs-max) {\r\n                text-align: center; \r\n            }\r\n        }\r\n    }\r\n    \r\n    // Iconblocks Widget //\r\n    \r\n    .blocks {\r\n        .icon {\r\n            font-size: 70px;\r\n        }\r\n    }\r\n    \r\n    \r\n    // Anchor widget //\r\n    \r\n    .anchor-widget {\r\n        padding-top: 30px;\r\n        @media (max-width: @screen-xs-max) {\r\n            padding: 50px 0 0 0;\r\n        }\r\n    }\r\n    \r\n    \r\n    // Social Media Widget //\r\n    .social-media-list {\r\n        img {\r\n            height: 40px;\r\n            margin: 20px;\r\n            &:hover {\r\n                transition: all 0.15s ease-in-out;\r\n                transform: scale(1.5, 1.5);\r\n            }\r\n        }\r\n    }\r\n    \r\n    \r\n    // phone special //\r\n    @media (max-width: 400px) {\r\n        .list-inline {\r\n            margin-bottom: 10px;\r\n        }\r\n        .widget-content {\r\n            padding: 7vw 0;\r\n        }\r\n    }\r\n\r\n}\r\n\r\n\r\n\r\n\r\n');
